    makeObservable(this, {
      name: observable,
      age: observable,
      married: observable,
      changeName: action,
      changeAge: action,
      toggleMarried: action
    });
  }

  changeName(value) {
    this.name = value;
  }

  changeAge(value) {
    this.age = value;
  }

  toggleMarried() {
    this.married = !this.married;
  }
}

